mapper.delete:
{
	CALL
		BEGIN
			DELETE FROM TB_USER WHERE USER_ID = #{user_id};
		
			DELETE FROM TB_USER_INFO WHERE USER_ID = #{user_id};
		
			DELETE FROM TB_USER_PW_OLD WHERE USER_ID = #{user_id};
		
			DELETE FROM TB_LIKE_LIST WHERE USER_ID = #{user_id};
		
			DELETE FROM TB_COOK_LIKE 
			WHERE COOK_SEQ IN (SELECT COOK_SEQ  FROM TB_COOK WHERE USER_ID = #{user_id});
		
			DELETE FROM TB_COOK_IMAGE 
			WHERE COOK_SEQ IN (SELECT COOK_SEQ  FROM TB_COOK WHERE USER_ID = #{user_id});
		
			DELETE FROM TB_COOK WHERE USER_ID = #{user_id};
		
			DELETE FROM TB_COOK_COMMENT WHERE USER_ID = #{user_id};
	END
}
